// src/OrderList.js








import React, { useEffect, useState } from "react";
import axios from "axios";
import { collection, onSnapshot } from "firebase/firestore";
import { db } from "./firebaseClient";

const OrderList = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);


async function testFirestoreConnection() {
  console.log("ðŸ“¡ Testing Firestore connection...");
  try {
    const querySnapshot = await getDocs(collection(db, "orders"));
    if (querySnapshot.empty) {
      console.warn("âš ï¸ No documents found in 'orders' collection!");
    } else {
      querySnapshot.forEach((doc) => {
        console.log("ðŸ”¥ Order:", doc.id, doc.data());
      });
    }
  } catch (error) {
    console.error("âŒ Firestore error:", error);
  }
}
testFirestoreConnection();








  // Fetch orders from server
 
  
  

 seEffect(() => {
  console.log("ðŸ” Setting up real-time Firestore listener...");

  //u Firestore live listener for the 'orders' collection
  const unsubscribe = onSnapshot(
    collection(db, "orders"),
    (snapshot) => {
      const liveOrders = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      console.log("ðŸ”¥ Live orders updated:", liveOrders);
      setOrders(liveOrders);
      setLoading(false);
    },
    (error) => {
      console.error("âŒ Firestore real-time error:", error);
    }
  );

  // Clean up listener when component unmounts
  return () => {
    console.log("ðŸ§¹ Unsubscribing from Firestore listener...");
    unsubscribe();
  };
}, []);




  // Update order status on server, then refresh local list
  const updateStatus = async (id, newStatus) => {
    try {
      await axios.patch(`http://localhost:5000/orders/${id}`, { status: newStatus });
      // Update local UI optimistically:
      setOrders((prev) =>
        prev.map((o) => (o.id === id ? { ...o, status: newStatus } : o))
      );
      // Optionally re-fetch from server:
      // await fetchOrders();
    } catch (err) {
      console.error("Error updating status:", err);
      // Optionally show notification to user
    }
  };

  if (loading) return <p>Loading orders...</p>;

  return (
    <div>
      {orders.length === 0 ? (
        <p>No orders yet.</p>
      ) : (
        orders.map((order) => (
          <div key={order.id} style={{ marginBottom: "10px" }}>
            <b>{order.user}</b> ordered <b>{order.item}</b> â€”{" "}
            <select
              value={order.status}
              onChange={(e) => updateStatus(order.id, e.target.value)}
              style={{ marginRight: "8px" }}
            >
              <option value="Accepted">Accepted</option>
              <option value="On the way">On the way</option>
              <option value="Delivered">Delivered</option>
            </select>

            <span className={`status ${order.status.toLowerCase().replace(/\s/g, "-")}`}>
              {order.status}
            </span>
          </div>
        ))
      )}
    </div>
  );
};

export default OrderList;
